// Production Environment Variable Injection Script
// This script should be generated by your build process or server
// to inject actual environment variables from Vercel

;(() => {
  console.log("[v0] Loading production environment variables...")

  // These values should be injected by your build process or server-side script
  const PRODUCTION_ENV = {
    NEXT_PUBLIC_SUPABASE_URL: process.env.NEXT_PUBLIC_SUPABASE_URL || "https://your-project.supabase.co",
    NEXT_PUBLIC_SUPABASE_ANON_KEY: process.env.NEXT_PUBLIC_SUPABASE_ANON_KEY || "your-anon-key",
  }

  // Inject into window.ENV
  window.ENV = window.ENV || {}
  Object.assign(window.ENV, PRODUCTION_ENV)

  // Update meta tags with actual values
  const urlMeta = document.querySelector('meta[name="supabase-url"]')
  const keyMeta = document.querySelector('meta[name="supabase-anon-key"]')

  if (urlMeta && PRODUCTION_ENV.NEXT_PUBLIC_SUPABASE_URL) {
    urlMeta.content = PRODUCTION_ENV.NEXT_PUBLIC_SUPABASE_URL
  }

  if (keyMeta && PRODUCTION_ENV.NEXT_PUBLIC_SUPABASE_ANON_KEY) {
    keyMeta.content = PRODUCTION_ENV.NEXT_PUBLIC_SUPABASE_ANON_KEY
  }

  console.log("[v0] âœ… Production environment variables injected")
})()
